"use strict";

var gulp = require('gulp');
var run = require( './' ); // called from within the gulp-run repo

gulp.task( 'create-hash',	function() {
	return run( 'git rev-parse HEAD' )
		.exec()
		.pipe( gulp.dest( '.' ) );
} );

gulp.task( 'test', [ 'create-hash' ], function() {
	return gulp.src( 'git' )
		.pipe( gulp.dest( 'git.hash' ) );
} );
